mapper.select:
select

from
t_performance_work_rt where occurrence_time between #{starttime} and
#{endtime}


	and cellid like #{cellid}


	and belong_area like #{work.belong_area}


	and monitor_content like #{work.monitor_content}


	and questionflag = #{work.questionflag}

SELECT

FROM
t_performance_work_rt WHERE occurrence_time > DATE_SUB((SELECT
occurrence_time FROM t_performance_work_rt 



		and cellid like #{cellid}


		and belong_area like #{work.belong_area}


		and monitor_content like #{work.monitor_content}


		and questionflag = #{work.questionflag}


ORDER BY occurrence_time DESC LIMIT 1),INTERVAL 7 DAY) 


	and cellid like #{cellid}


	and belong_area like #{work.belong_area}


	and monitor_content like #{work.monitor_content}


	and questionflag = #{work.questionflag}

SELECT

FROM
t_performance_work_rt WHERE occurrence_time >
DATE_SUB((SELECT
occurrence_time FROM t_performance_work_rt 



		and cellid like #{cellid}


		and belong_area like #{work.belong_area}


		and monitor_content like #{work.monitor_content}


		and questionflag = #{work.questionflag}


ORDER BY occurrence_time DESC LIMIT 1),INTERVAL 30 DAY)


	and cellid like #{cellid}


	and belong_area like #{work.belong_area}


	and monitor_content like #{work.monitor_content}


	and questionflag = #{work.questionflag}

select

from
t_performance_work_rt WHERE occurrence_time = (SELECT
occurrence_time FROM t_performance_work_rt 



		and cellid like #{cellid}


		and belong_area like #{work.belong_area}


		and monitor_content like #{work.monitor_content}


		and questionflag = #{work.questionflag}


ORDER BY occurrence_time
DESC LIMIT 1)


	and cellid like #{cellid}


	and belong_area like #{work.belong_area}


	and monitor_content like #{work.monitor_content}


	and questionflag = #{work.questionflag}
